from xbmctorrent import plugin, scrapers

from xbmctorrent.scrapers import \
    MODULES, \
    tpb, \
    eztv, \
    kickass

@plugin.route("/")
def index():
    for module in MODULES:
        yield {
            "label": module["name"],
            "thumbnail": module["image"],
            "path": plugin.url_for(module["view"]),
            "is_playable": False,
        }
