from xbmctorrent import plugin, magnet, scrapers
from xbmctorrent.ga import tracked
from xbmctorrent.scrapers import \
    MODULES, \
    eztv, \
    yify, \
    kickass, \
    tpb


@plugin.route("/")
@tracked(force=True)
def index():
    for module in MODULES:
        yield {
            "label": module["name"],
            "thumbnail": module["image"],
            "path": plugin.url_for(module["view"]),
        }
