from xbmctorrent import plugin, scrapers

from xbmctorrent.scrapers import \
    MODULES, \
    eztv, \
    yify, \
    kickass, \
    tpb

from xbmctorrent.ga import tracked, track_event

@plugin.route("/")
@tracked(force=True)
def index():
    for module in MODULES:
        yield {
            "label": module["name"],
            "thumbnail": module["image"],
            "path": plugin.url_for(module["view"]),
            "is_playable": False,
        }
